import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { addQuizId } from '../topics/topicsSlice';

export const createNewQuiz = createAsyncThunk(
    'quizzes/createNewQuiz',
    async (payload) => {
        const quiz = await addQuiz(payload);
        const addId = await addQuizId(quiz.id);

        return addId;
    }
)

export const quizzesSlice = createSlice({
    name: 'quizzes',
    initialState: { quizzes: {} },
    reducers: {
        addQuiz: (state, action) => {
            const newQuiz = action.payload;
            state.quizzes = {
                ...state.quizzes,
                [newQuiz.id]: {
                    id: newQuiz.id,
                    topicId: newQuiz.topicId,
                    name: newQuiz.name,
                    cardIds: newQuiz.cardIds
                }
            }
        }
    }
})

export const selectQuiz = (state) => state.quizzes;
export const { addQuiz } = quizzesSlice.actions;

export default quizzesSlice.reducer;
